function submitLogin(e){fetch("/users/login",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({email:e.srcElement[0].value,password:e.srcElement[1].value})}).then(async e=>{Swal.fire({toast:!0,position:"top-end",type:e.ok?"success":"error",text:await e.text(),showConfirmButton:!1,timer:3e3,background:"#2a2c2e"}),e.ok&&setTimeout(()=>document.location="/users",1e3)})}$(".message a").click(function(){$("form").animate({height:"toggle",opacity:"toggle"},"slow")}),document.querySelector(".login-form").addEventListener("submit",e=>{e.preventDefault(),submitLogin(e)});